import { Link, Route, Routes, useNavigate } from "react-router-dom";
import { useState } from "react";
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Addproducts from "./Components/AddProduct/AddProducts";
import ViewProducts from "./Components/ViewProduct/ViewProducts";
import EditProduct from "./Components/EditProduct/EditProduct";
import AdminLogin from "./Components/AdminLogin/AdminLogin";
import ViewCustomer from "./Components/ViewCustomer/ViewCustomer";
import ViewDeliveryPartner from "./Components/ViewDeliveryPartner/ViewDeliveryPartner";
import './App.css';

function App() {
  const [isAdmin, setIsAdmin] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const navigate = useNavigate();
  return (
    <div>
      <nav className="navbar">
        {isAdmin ? (
          <>
            <button className="nav-btn" onClick={() => navigate("/")}>View Food</button>
            <button className="nav-btn" onClick={() => navigate("/addProducts")}>Add Food</button>
            <button className="nav-btn" onClick={() => navigate("/viewCustomer")}>View Customer</button>
            <button className="nav-btn" onClick={() => navigate("/viewDeliveryPartner")}>View Delivery Partner</button>
            <button className="nav-btn logout-btn" onClick={() => { setIsAdmin(false); navigate("/admin/login"); }}>Logout</button>
          </>
        ) : null}
      </nav>

      <Routes>
        <Route path="/admin/login" element={<AdminLogin setIsAdmin={setIsAdmin}/>} />
        {isAdmin && <Route path="/addProducts" element={<Addproducts />} />}
        {isAdmin && <Route path="/" element={<ViewProducts />} />}
        {isAdmin && <Route path="/editProduct/:id" element={<EditProduct />} />}
        {isAdmin && <Route path="/viewCustomer" element={<ViewCustomer />} />}
        {isAdmin && <Route path="/viewDeliveryPartner" element={<ViewDeliveryPartner />} />}
      </Routes>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={true}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
        style={{ zIndex: 9999 }}
      />
    </div>
  );
}

export default App;
